INSERT INTO `layouts` (`id`, `layoutname`, `layoutcreator`, `layoutdescription`, `layoutqpt`, `layoutpreview`, `byuser`, `istiny`, `issmall`, `ismedium`, `islarge`, `isscreen`, `popular`) VALUES
(1, 'Index map A4 Portrait', 'Klas Karlsson', 'Map with index grid', '../layouts/resources/5f2ea54ce442b5.33477222.qpt', '../layouts/images/5f2ea54ce44388.34804648.jpg', 'klakar', NULL, 1, NULL, NULL, NULL, NULL),
(2, 'Full HD screen with text area', 'Klas Karlsson', 'This layout has the resolution 1920x1080 and might be suitable for generating a presentation slide. It has a title placeholder and an area for some text.', '../layouts/resources/5f2ea5f98a53e1.56763458.qpt', '../layouts/images/5f2ea5f98a5464.63480178.jpg', 'klakar', NULL, NULL, NULL, NULL, 1, NULL),
(3, 'Standard A3 Landscape with grid', 'Klas Karlsson', 'Created in 1:50\'000 scale with 1 km grids suitable for UTM projections.\r\nA lot of map layout information in the margins, as well as a text area for additional information.', '../layouts/resources/5f2ea70884d101.74067897.qpt', '../layouts/images/5f2ea70884d192.51494044.jpg', 'klakar', NULL, NULL, 1, NULL, NULL, NULL),
(4, 'Standard A3 Portrait with grid', 'Klas Karlsson', 'Created in 1:50\'000 scale with 1 km grids suitable for UTM projections. A lot of map layout information in the margins, as well as a text area for additional information.', '../layouts/resources/5f2eae93174493.47577054.qpt', '../layouts/images/5f2eae93174540.49168187.jpg', 'klakar', NULL, NULL, 1, NULL, NULL, NULL),
(5, 'Drawing A1 Landscape - information bottom right', 'Klas Karlsson', 'This layout can be used in QGIS as a simple CAD drawing substitution. It has a simple information box in the bottom right. It is meant for extremely large scale maps and is default set to 1:400.', '../layouts/resources/5f2eafcd518c84.31834074.qpt', '../layouts/images/5f2eafcd518d27.51669229.png', 'klakar', NULL, NULL, NULL, 1, NULL, NULL),
(6, 'Postcard CSS Title', 'Klas Karlsson', ' Simple postcard with title styled with CSS. It is simplest to use the \"title\" variable in the layout to change the title text.', '../layouts/resources/5f2eb619e1eda7.23049489.qpt', '../layouts/images/5f2eb619e1ee50.29674482.png', 'klakar', 1, NULL, NULL, NULL, NULL, 1),
(7, 'Standard A4 Landscape with grid', 'Klas Karlsson', 'Created in 1:50\'000 as default, but with grids prepared for 1, 5 and 10 km interval, as well as 5 and 15 minutes lat/long. This orientation leaves room for some extra text to the right.', '../layouts/resources/5f2f99abe5c471.47526868.qpt', '../layouts/images/5f2f99abe5c4e0.37617526.jpg', 'klakar', NULL, 1, NULL, NULL, NULL, NULL),
(8, 'Standard A4 Portrait with grid', 'Klas Karlsson', 'Standard projected map layout with prepared grids with 1, 5 and 10 km interval, as well as 5 and 15 minutes lat/long. A lot of extra information about the map is placed in the margins. The map is default at 1:50\'000.', '../layouts/resources/5f2f9a8be01496.24803415.qpt', '../layouts/images/5f2f9a8be01522.74046234.jpg', 'klakar', NULL, 1, NULL, NULL, NULL, NULL),
(9, 'Instagram format 1:1', 'Klas Karlsson', 'This square layout is created to fit the Instagram 1080x1080 format. It is easy to change Title and Subtitle with variables in the layout settings.', '../layouts/resources/5f2f9ea669c487.31156090.qpt', '../layouts/images/5f2f9ea669c4f4.16900436.jpg', 'klakar', NULL, NULL, NULL, NULL, 1, NULL);
