(1, 'Panel', 'Klas Karlsson', ' Resembling old wood inlay panels in style. Easily change color, size and orientation.', 'styles/resources/panel.xml', 'styles/images/Screenshot from 2020-07-15 18-22-05.png', 'klakar', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL),
(2, 'Dormido Rough Circles', 'Klas Karlsson', '  SVG style based on Hannah Dormido hand drawn map style with circles. You can easily change colors, size and orientation for the pattern.', 'styles/resources/dormido_rough.xml', 'styles/images/Screenshot from 2020-07-16 10-12-36.png', 'klakar', NULL, NULL, 1, NULL, NULL, NULL, NULL, 1),
